/*CREATE DATABASE SpotiTube;*/
USE SpotiTube;

CREATE TABLE User(
username VARCHAR(30) NOT NULL,
password VARCHAR(128) NOT NULL,
CONSTRAINT pk_user PRIMARY KEY (username)
);

CREATE TABLE Playlist(
id int(11) NOT NULL AUTO_INCREMENT,
name VARCHAR(250) NOT NULL,
owner VARCHAR(30) NOT NULL,
CONSTRAINT pk_playlist PRIMARY KEY (id),
CONSTRAINT ak_playlist UNIQUE (name, owner),
CONSTRAINT fk_user FOREIGN KEY (owner) REFERENCES User(username)
);

CREATE TABLE Track(
id int(11) NOT NULL AUTO_INCREMENT,
title VARCHAR(250) NOT NULL,
performer VARCHAR(30) NOT NULL,
duration LONG,
album VARCHAR(250) NULL,
play_count INT NULL,
publication_date DATETIME NULL,
description VARCHAR(250) NULL,
CONSTRAINT pk_track PRIMARY KEY (id),
CONSTRAINT ak_track UNIQUE (title, performer)
);

CREATE TABLE PlaylistTrack(
playlist_id int(11) NOT NULL,
track_id int(11) NOT NULL,
offline_available BOOL DEFAULT FALSE NOT NULL,
CONSTRAINT pk_playlist_track PRIMARY KEY (playlist_id, track_id),
CONSTRAINT fk_pt_playlist FOREIGN KEY (playlist_id) REFERENCES Playlist(id) ON UPDATE CASCADE ON DELETE CASCADE,
CONSTRAINT fk_pt_track FOREIGN KEY (track_id) REFERENCES Track(id) ON UPDATE CASCADE ON DELETE CASCADE
);

